import React, { useState } from 'react';
import AddTodo from './components/AddTodo';
import TodoList from './components/TodoList';
import ArchivedTodos from './components/ArchivedTodos';
import { Container, Typography, Button, Collapse, Box } from '@mui/material';

const App = () => {
  const [showArchived, setShowArchived] = useState(false);

  const toggleArchived = () => {
    setShowArchived(!showArchived);
  };

  return (
    <Container>
      <Box 
        display="flex" 
        flexDirection="column" 
        alignItems="center" 
        justifyContent="center" 
        minHeight="100vh"
      >
        <Typography variant="h3" gutterBottom>
          Todo List React/Redux/MUI
        </Typography>
        <Box width="100%" display="flex" justifyContent="center" mb={2}>
          <AddTodo />
        </Box>
        <Box width="100%" display="flex" justifyContent="center">
          <TodoList />
        </Box>
        <Button variant="contained" color="secondary" onClick={toggleArchived} style={{ marginTop: '20px' }}>
          {showArchived ? 'Hide' : 'Show'} Archived Todos
        </Button>
        <Collapse in={showArchived} style={{ width: '100%', marginTop: '20px' }}>
          <ArchivedTodos />
        </Collapse>
      </Box>
    </Container>
  );
};

export default App;
